#pragma MESSAGE DISABLE C4002          /* Disable warning C4002 "Result not used" */

static bool EnUser;                    /* Enable device by user */
static word CmpVal;                    /* Value periodicly addded to compare register */
/*
** ===================================================================
**     Method      :  HWEnDi (bean TimerInt)
**
**     Description :
**         Enables or disables the peripheral(s) associated with the bean.
**         The method is called automatically as a part of the Enable and 
**         Disable methods and several internal methods.
**         This method is internal. It is used by Processor Expert only.
** ===================================================================
*/
static void HWEnDi(void)
{
  if (EnUser) {
    TPM2C1V=TPM2CNT + CmpVal;          /* Count current value for the compare register */
    TPM2C1SC_CH1F = 0;                 /* Reset request flag and */
    TPM2C1SC_CH1IE = 1;                /* Enable interrupt by compare register */
  } else {
    TPM2C1SC_CH1IE = 0;                /* Disable interrupt by compare register */
  }
}

