ISR(AudioLoader_Interrupt)
{
  TPM2C1V+=CmpVal;                     /* Count and save new value to the compare register */
  TPM2C1SC_CH1F = 0;                   /* Reset compare interrupt request flag */
  AudioLoader_OnInterrupt();           /* Invoke user event */
}


